;&gt;if&lt;/span&gt; (isValid) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      &lt;span class=&#34;comment&#34;&gt;// Save the entered instance name to a cookie&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      &lt;span class=&#34;title function_&#34;&gt;setCookie&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;mastodon-instance&amp;quot;&lt;/span&gt;, eInstance.&lt;span class=&#34;property&#34;&gt;value&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      &lt;span class=&#34;comment&#34;&gt;// Generate the share URL for the instance&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      &lt;span class=&#34;keyword&#34;&gt;let&lt;/span&gt; shareUrl = &lt;span class=&#34;string&#34;&gt;`https://&lt;span class=&#34;subst&#34;&gt;$&amp;#123;eInstance.value&amp;#125;&lt;/span&gt;/share?text=&lt;span class=&#34;subst&#34;&gt;$&amp;#123;&lt;span class=&#34;built_in&#34;&gt;encodeURIComponent&lt;/span&gt;(eText.value)&amp;#125;&lt;/span&gt;`&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      &lt;span class=&#34;comment&#34;&gt;// Open the share URL in a new window and close the dialog&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      &lt;span class=&#34;variable language_&#34;&gt;window&lt;/span&gt;.&lt;span class=&#34;title function_&#34;&gt;open&lt;/span&gt;(shareUrl, &lt;span class=&#34;string&#34;&gt;&amp;#x27;_blank&amp;#x27;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      dpDialog.&lt;span class=&#34;property&#34;&gt;base&lt;/span&gt;.&lt;span class=&#34;property&#34;&gt;element&lt;/span&gt;.&lt;span class=&#34;title function_&#34;&gt;downupPopup&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;close&amp;quot;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;